#' optimize_hybrid(f, init = 0, lower = -Inf, upper = 1, maximize = FALSE)
#' optimize_hybrid(f, init = 0, lower = 0, upper = Inf, maximize = FALSE)
#' optimize_hybrid(f, init = 0, lower = -Inf, upper = 0, maximize = FALSE)
#'
